import { useState } from "react";
import "./QRBlock.scss";

const QRBlock = () => {
  const [isActive, setIsActive] = useState(false);
  // const QRCube = "/icons/material-symbols_view-in-ar-outline-rounded.svg";
  const QR_Code = "/icons/QR-code.svg";
  const close_button = "/icons/close-icon.svg";

  const handleActiveStatusChange = () => {
    setIsActive(!isActive);
  };

  return (
    <>
      <div className="QR-button">
        <button
          className="QR-button__main"
          onClick={handleActiveStatusChange}
        >
          <img
            src={QRCube}
            alt="QR Cube Icon"
          />

          <p>See In Real Life</p>
        </button>
      </div>

      {isActive && (
        <div className="QR-block">
          <div className="QR-block__description">
            <div
              className="QR-block__description__close"
              onClick={handleActiveStatusChange}
            >
              <img
                src={close_button}
                alt="Close Icon"
              />
            </div>

            <p>
              Scan the QR code with your phone. Within 1-3 seconds the AR
              function opens on your phone.
            </p>

            <hr></hr>

            <img
              src={QR_Code}
              alt="QR Code Icon"
              className="QR-block__description__img"
            />
          </div>
        </div>
      )}
    </>
  );
};

export default QRBlock;
