import React, { useRef } from 'react';
import { Canvas } from '@react-three/fiber';
import { useLoader } from '@react-three/fiber';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { TextureLoader } from 'three';
import * as THREE from "three";

const scene = new THREE.Scene();
  const camera = new THREE.PerspectiveCamera(
    75,
    window.innerWidth / window.innerHeight,
    0.1,
    1000
  );
function Backpack() {
  const gltf = useLoader(GLTFLoader, '/models/backpack.glb');
  const baseColor = useLoader(TextureLoader, '/textures/denim_baseColor.jpg');
  const normalMap = useLoader(TextureLoader, '/textures/denim_normal.jpg');
  const roughnessMap = useLoader(TextureLoader, '/textures/denim_occlusionRoughnessMetallic.jpg');

  return (
    <primitive
      object={gltf.scene}
      dispose={null}
      // Применение материала к модели
      onPointerDown={() => {
        gltf.scene.traverse((child) => {
          if (child.isMesh) {
            child.material.map = baseColor;
            child.material.normalMap = normalMap;
            child.material.roughnessMap = roughnessMap;
            child.material.needsUpdate = true;
          }
        });
      }}
    />
  );
}

export default function App() {
  return (
    <div>
      <header>3d Project</header>

      <Canvas>
        {/* <ambientLight intensity={0.5} /> */}
        <directionalLight position={[10, 10, 5]}  />
        <Backpack />
      </Canvas>
    </div>
  );
}
