import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { TextureLoader } from "three";
import { useLoader } from "@react-three/fiber";

const Backpack = ({ bodyColor, material, metalColor }) => {
  const glb = useLoader(GLTFLoader, "/models/backpack.glb");
  const baseColor = useLoader(
    TextureLoader,
    `/textures/${material}_baseColor.jpg`
  );
  const normalMap = useLoader(
    TextureLoader,
    `/textures/${material}_normal.jpg`
  );
  const roughnessMap = useLoader(
    TextureLoader,
    `/textures/${material}_occlusionRoughnessMetallic.jpg`
  );
  // const metalTexture = useLoader(TextureLoader, metalColor);
  const metalTexture = useLoader(TextureLoader, `/textures/${metalColor}_baseColor.jpg`);
  const metalRoughnessMap = useLoader(TextureLoader, `/textures/${metalColor}_occlusionRoughnessMetallic.jpg`);

  console.log("glb === ", glb);
  glb.scene.traverse((child) => {
    if (child.isMesh) {
      child.material.map = baseColor;
      child.material.normalMap = normalMap;
      child.material.roughnessMap = roughnessMap;

      if (child.material.name === "metall") {
        child.material.map = metalTexture;
        child.material.roughnessMap = metalRoughnessMap;
      }

      child.material.needsUpdate = true;
    }
  });
  glb.materials.body.color.set(bodyColor);
  // glb.materials.metall.map = "/textures/metall_occlusionRoughnessMetallic.jpg";
  glb.materials.strap.color.set(bodyColor);

  glb.scene.position.set(0, 0, 0);
  glb.scene.scale.set(5, 5, 5);

  return (
    <primitive
      object={glb.scene}
      dispose={null}
    />
  );
}

export default Backpack;
