import React, { useState } from "react";
import { Canvas, useLoader } from "@react-three/fiber";

import { OrbitControls } from "@react-three/drei";
import "./app.scss";



export default function App() {
  const [bodyColor, setBodyColor] = useState("#8B4512");
  const [material, setMaterial] = useState("denim");
  const [metallColor, setMetallColor] = useState(`#fff`);
  const body_colors = {
    Brown: "#8B4512",
    Black: "#1A1A1A",
    Blue: "#104D97",
  };

  const handleColorChange = (color) => {
    setBodyColor(body_colors[color]);
  };

  return (
    <div className="body">
      <header>3d module</header>

      <Canvas
        camera={{ position: [0, 2, 10], fov: 50 }}
        style={{ height: "100vh", width: "100vw" }}
      >
        <ambientLight
          intensity={0.5}
          color={"#fff"}
        />
        <directionalLight
          position={[10, 10, 10]}
          intensity={1}
          color={"#fff"}
        />
        <Backpack
          bodyColor={bodyColor}
          material={material}
          metallColor={metallColor}
        />
        <OrbitControls />
      </Canvas>

      <div className="variosity">
        <div className="block">
          <p>BODY COLOR</p>

          <div className="body-color">
            {Object.keys(body_colors).map((body, index) => (
              <div
                className="body-color--change"
                key={index}
                onClick={() => handleColorChange(body)}
              >
                <button
                  className={`body-color--change__button ${
                    bodyColor === body_colors[body] ? "active" : ""
                  }`}
                >
                  <div
                    className={`body-color--change__background`}
                    style={{ backgroundColor: body_colors[body] }}
                  ></div>
                </button>

                <p
                  className={`body-color--change__text ${
                    bodyColor === body_colors[body] ? "active" : ""
                  }`}
                >
                  {body}
                </p>
              </div>
            ))}
          </div>
        </div>

        {/* 
        <div className="metal-color">
          <p>METAL COLOR</p>
          <button>Silver</button>
          <button>Gold</button>
          <button>Black</button>
        </div>
        <div className="material">
          <p>MATERIAL</p>
          <button>denim</button>
          <button>fabric</button>
          <button>leather</button>
          <button>metall</button>
          <button>strap</button>
        </div>
        */}
      </div>
    </div>
  );
}
