import React, { useState } from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls } from "@react-three/drei";
import "./app.scss";
import Backpack from "./component/Backpack";
// import QRCube from "../public/icons/material-symbols_view-in-ar-outline-rounded.svg";

export default function App() {
  const body_colors = {
    Brown: "#8B4512",
    Black: "#1A1A1A",
    Blue: "#104D97",
  };
  const metal_colors = {
    Silver: "/metal-color/silver.png",
    Gold: "/metal-color/gold.png",
    Black: "/metal-color/black.png",
  };
  const materials = {
    denim: "denim",
    fabric: "fabric",
    leather: "leather",
  };
  const [bodyColor, setBodyColor] = useState(body_colors.Brown);
  const [material, setMaterial] = useState(materials.denim);
  const [metalColor, setMetalColor] = useState(metal_colors.Silver);
  const [isActive, setIsActive] = useState(false);

  const handleColorChange = (color) => {
    setBodyColor(body_colors[color]);
  };

  const handleMetalColorChange = (color) => {
    setMetalColor(color);
  };

  const handleMaterialChange = (mat) => {
    setMaterial(mat);
  };

  const handleActiveStatusChange = () => {
    setIsActive(!isActive);
  };

  const QRCube = "/icons/material-symbols_view-in-ar-outline-rounded.svg";
  const QR_Code = "/icons/QR-code.svg";
  return (
    <div className="body">
      <div className="QR-button">
        <button
          className="QR-button__main"
          onClick={handleActiveStatusChange}
        >
          <img
            src={QRCube}
            alt="QR Cube Icon"
          />

          <p>See In Real Life</p>
        </button>
      </div>

      {isActive && (
        <div className="QR-block">
          <img
            src={QR_Code}
            alt="QR Code Icon"
          />

          <hr></hr>

          <p>
            Scan the QR code with your phone. Within 1-3 seconds the AR function
            opens on your phone.
          </p>
        </div>
      )}

      <Canvas
        camera={{ position: [0, 2, 10], fov: 50 }}
        style={{ height: "100vh", width: "100vw" }}
      >
        <ambientLight
          intensity={0.5}
          color={"#fff"}
        />
        <directionalLight
          position={[10, 10, 10]}
          intensity={1}
          color={"#fff"}
        />
        <Backpack
          bodyColor={bodyColor}
          material={material}
          metalColor={metalColor}
        />
        <OrbitControls />
      </Canvas>

      <div className="variety">
        <div className="block">
          <div className="block--title">
            <p>BODY COLOR</p>
          </div>

          <hr></hr>

          <div className="body-buttons">
            {Object.keys(body_colors).map((body, index) => (
              <div
                className="body-buttons--change"
                key={index}
                onClick={() => handleColorChange(body)}
              >
                <button
                  className={`body-buttons--main__button ${
                    bodyColor === body_colors[body] ? "active" : ""
                  }`}
                >
                  <div
                    className={`body-buttons--main__button__background`}
                    style={{ backgroundColor: body_colors[body] }}
                  ></div>
                </button>

                <p
                  className={`body-buttons--main__text ${
                    bodyColor === body_colors[body] ? "active" : ""
                  }`}
                >
                  {body}
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="block">
          <div className="block--title">
            <p>METAL COLOR</p>
          </div>

          <hr></hr>

          <div className="body-buttons">
            {Object.keys(metal_colors).map((body, index) => (
              <div
                className="body-buttons--change"
                key={index}
                onClick={() => handleMetalColorChange(metal_colors[body])}
              >
                <button
                  className={`body-buttons--main__button ${
                    metalColor === metal_colors[body] ? "active" : ""
                  }`}
                >
                  <div
                    className={`body-buttons--main__button__background`}
                    style={{ backgroundImage: `url(${metal_colors[body]})` }}
                  ></div>
                </button>

                <p
                  className={`body-buttons--main__text ${
                    metalColor === metal_colors[body] ? "active" : ""
                  }`}
                >
                  {body}
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="material">
          <div className="material--title">
            <p>MATERIAL</p>
          </div>

          <hr></hr>

          <div className="material--buttons">
            {Object.keys(materials).map((body, index) => (
              <div key={body}>
                <button
                  className={`material--buttons__main ${
                    material === materials[body] ? "active" : ""
                  }`}
                  onClick={() => handleMaterialChange(body)}
                >
                  <p
                    className={`material--buttons__main__text ${
                      material === materials[body] ? "active" : ""
                    }`}
                  >
                    {body}
                  </p>
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
