const ColorsBlock = (setColor, mainColor, mainColors) => {
  const handleColorChange = (color) => {
    setColor(mainColors[color]);
  };

  return (
    <div className="block">
      <div className="block--title">
        <p>BODY COLOR</p>
      </div>

      <hr></hr>

      <div className="body-buttons">
        {Object.keys(mainColors).map((body, index) => (
          <div
            className="body-buttons--change"
            key={index}
            onClick={() => handleColorChange(body)}
          >
            <button
              className={`body-buttons--main__button ${
                bodyColor === mainColors[body] ? "active" : ""
              }`}
            >
              <div
                className={`body-buttons--main__button__background`}
                style={{ backgroundColor: mainColors[body] }}
              ></div>
            </button>

            <p
              className={`body-buttons--main__text ${
                bodyColor === mainColors[body] ? "active" : ""
              }`}
            >
              {body}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ColorsBlock;
