import React, { useState } from "react";
import { Canvas, useLoader } from "@react-three/fiber";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { TextureLoader } from "three";
import { OrbitControls } from "@react-three/drei";
import "./app.scss";



function Backpack() {
  const glb = useLoader(GLTFLoader, "/models/backpack.glb");
  const baseColor = useLoader(
    TextureLoader,
    `/textures/${material}_baseColor.jpg`
  );
  const normalMap = useLoader(
    TextureLoader,
    `/textures/${material}_normal.jpg`
  );
  const roughnessMap = useLoader(
    TextureLoader,
    `/textures/${material}_occlusionRoughnessMetallic.jpg`
  );

  // console.log("glb === ", glb);
  glb.scene.traverse((child) => {
    if (child.isMesh) {
      child.material.map = baseColor;
      child.material.normalMap = normalMap;
      child.material.roughnessMap = roughnessMap;
      child.material.needsUpdate = true;
    }
  });
  glb.materials.body.color.set(bodyColor);
  glb.materials.strap.color.set(bodyColor);
  glb.materials.metall.color.set(metallColor);

  glb.scene.position.set(0, 0, 0);
  glb.scene.scale.set(5, 5, 5);

  return (
    <primitive
      object={glb.scene}
      dispose={null}
    />
  );
}

const body_color = ["Brown", "Black", "Blue"];
export default function App() {
  return (
    <div className="body">
      <header>3d module</header>

      <Canvas
        camera={{ position: [0, 2, 10], fov: 50 }}
        style={{ height: "100vh", width: "100vw" }}
      >
        <ambientLight
          intensity={0.5}
          color={"#fff"}
        />
        <directionalLight
          position={[10, 10, 10]}
          intensity={1}
          color={"#fff"}
        />
        <Backpack />
        <OrbitControls />
      </Canvas>

      <div className="variosity">
        <div className="block">
          <p>BODY COLOR</p>
          <div className="body-color">
            {body_color.map((body, index) => (
              <div
                className="body-color--change"
                key={index}
              >
                <button
                  className="body-color--change__button"
                  style={{ backgroundColor: body }}
                ></button>
                <p
                  className={`body-color--change__text ${
                    bodyColor === 0x8b4513 ? "active" : ""
                  }`}
                >
                  {body}
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="metal-color">
          <p>METAL COLOR</p>
          <button>Silver</button>
          <button>Gold</button>
          <button>Black</button>
        </div>
        <div className="material">
          <p>MATERIAL</p>
          <button>denim</button>
          <button>fabric</button>
          <button>leather</button>
          <button>metall</button>
          <button>strap</button>
        </div>
      </div>
    </div>
  );
}
